tinymce.PluginManager.add('youtube',function(editor){function openmanager(){var title="Choose YouTube Video";if(typeof editor.settings.youtube_title!=="undefined"&&editor.settings.youtube_title){title=editor.settings.youtube_title;}
win=editor.windowManager.open({title:title,file:tinyMCE.baseURL+'/plugins/youtube/youtube.html',filetype:'video',width:785,height:560,inline:1,buttons:[{text:'cancel',onclick:function(){this.parent().parent().close();}}]});}
editor.addButton('youtube',{icon:true,image:tinyMCE.baseURL+'/plugins/youtube/icon.png',tooltip:'InsertYoutube video',shortcut:'Ctrl+Q',onclick:openmanager});editor.addShortcut('Ctrl+Q','',openmanager);editor.addMenuItem('youtube',{icon:'media',text:'Insert Youtube video',shortcut:'Ctrl+Q',onclick:openmanager,context:'insert'});});